mapper.select:
select
from user
${conditionSQL.conditions}
AND is_deleted != 1
${conditionSQL.sort}
ORDER BY create_date ASC

select count(1) from user
${conditionSQL.conditions}
AND is_deleted != 1

mapper.delete:
update user
SET is_deleted = 1,
delete_user_id = #{deleteUserId,jdbcType=BIGINT},
delete_date = #{deleteDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
